    useEffect( () => {
        const champions = ["Tryndamere", "Jax"]
        champions.forEach(async champion => {
            await postrequest(champion)
        });
        async function postrequest(champion) {
            const data = await axios({
                url: 'http://localhost:5000/graphql',
                method: 'post',
                data: {
                  query: `
                    query Champion {
                      champion(name: ${champion}) {
                        id
                        name
                        image {
                            full
                        }
                        }
                      }
                    `
