        async function postrequest() {
            const champions = ["Tryndamere", "Jax", "Khazix"]
            const p = champions.map((champ) => {
                return axios({
                    url: 'http://localhost:5000/graphql',
                    method: 'post',
                    data: {
                        query: `
                        query Champion {
                            champion(name: "${champ}") {
                            name
                            image {
                                full
                            }
                            }
                            }
