import React, { Fragment, useEffect } from 'react'
const axios = require("axios")

// import { Link } from 'react-router-dom'
//Call data.keys() on https://ddragon.leagueoflegends.com/cdn/6.24.1/data/en_US/champion.json to grab list of champions.
function DisplayChampions() {
    async function postrequest(champion) {
        return await axios({
            url: 'http://localhost:5000/graphql',
            method: 'post',
            data: {
                query: `
                query Champion {
                    champion(name: "${champion}") {
                    name
                    image {
                        full
                    }
                    }
                    }
                `
            }
            })
    }
    function getData() {
        const champions = ["Tryndamere", "Jax", "Khazix"]
        const data = []
        champions.forEach(async champion => {
            // console.log(champion)
            const tempData = await postrequest(champion)
            data.push(tempData.data.data.champion)
        });
        console.log(data)
        return data
    }
    function displayIcons() {
        const champions = getData()
        console.log(champions)
        return (
            <ul>
                {champions.map((champion) => {
                    console.log(`Champion Variable: ${champion}`)
                    return (
                        <li>
                            <h1>Test</h1>
                            <img src={`http://ddragon.leagueoflegends.com/cdn/10.21.1/img/champion/${champion}`}/>
                            <h1>{champion}</h1>

                        </li>
                    )
            })}

            </ul>
        )
    }
    return (
        <div>
            {displayIcons()}
        </div>
    )
}
export default DisplayChampions
